func process(input: String) -> String {
    let keypad = [[1,2,3],[4,5,6],[7,8,9]]
    func number(current: (Int,Int), line: String) -> (Int,Int) {
        var current = current
        for i in line.characters {
            switch i {
            case "R": if current.1 < 2 { current.1 += 1 }
            case "L": if current.1 > 0 { current.1 -= 1 }
            case "D": if current.0 < 2 { current.0 += 1 }
            case "U": if current.0 > 0 { current.0 -= 1 }
            default: break
            }
        }
        return current
    }
    let lines = input.components(separatedBy: ",")
    var str = ""
    var current = (1,1)
    for line in lines {
        current = number(current: current, line: line)
        str += "\(keypad[current.0][current.1])"
    }
    return str
}

let testInput = "ULL,RRDDD,LURDL,UUUUD"
let input = ",,,,"

process(input: input)
