
func process(input: String) -> String {
    let keypad = [["0","0","1","0","0"],
                  ["0","2","3","4","0"],
                  ["5","6","7","8","9"],
                  ["0","A","B","C","0"],
                  ["0","0","D","0","0"]]
    func number(current: (Int,Int), line: String) -> (Int,Int) {
        var current = current
        func available(test: (Int, Int)) -> Bool {
            let inRange = test.0 >= 0 && test.0 < 5 && test.1 >= 0 && test.1 < 5
            return inRange && keypad[test.0][test.1] != "0"
        }
        for i in line.characters {
            switch i {
            case "R": if available(test: (current.0, current.1+1)) { current.1 += 1 }
            case "L": if available(test: (current.0, current.1-1)) { current.1 -= 1 }
            case "D": if available(test: (current.0+1, current.1)) { current.0 += 1 }
            case "U": if available(test: (current.0-1, current.1)) { current.0 -= 1 }
            default: break
            }
        }
        return current
    }
    let lines = input.components(separatedBy: ",")
    var str = ""
    var current = (2,0)
    for line in lines {
        current = number(current: current, line: line)
        str += "\(keypad[current.0][current.1])"
    }
    return str
}

let testInput = "ULL,RRDDD,LURDL,UUUUD"
let input = ",,,,"

process(input: input)
